<?php

namespace Agile\NimbleBoardBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * ProjectRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProjectRepository extends EntityRepository
{
  public function findOneByIdJoinedToStories($id)
  {
    $query = $this->getEntityManager()->createQuery('
      select p, s from NimbleBoardBundle:Project p
      left join p.stories s
      where p.id = :id
    ')->setParameter('id', $id);
    try {
      return $query->getSingleResult();
    } catch (\Doctrine\ORM\NoResultException $e) {
      return null;
    }
  }

  public function findOneByIdJoinedToSprints($id)
  {
    $query = $this->getEntityManager()->createQuery('
      select p, sp from NimbleBoardBundle:Project p
      left join p.sprints sp
      where p.id = :id
      order by sp.id desc
    ')->setParameter('id', $id);
    try {
      return $query->getSingleResult();
    } catch (\Doctrine\ORM\NoResultException $e) {
      return null;
    }
  }
}
